import React from 'react';

class SetTimer extends React.Component {

    constructor(props) {
        super(props);       

        this.state = { 
            hours: this.props.hours,
            minutes: this.props.minutes,
            seconds: this.props.seconds,
            }; 

    }

    //Format the time
    formatTime(time) {
        return time < 10 ? (`0${time}`) : time;
    }

    render()
    {
        return (
            <div>
                <input id="hours" type="number" value={this.state.hours}></input>
                <input id="minutes" type="number" value={this.state.minutes}></input>
                <input id= "seconds" type="number" value={this.state.seconds}></input>
            </div>
        );
    }
}


SetTimer.defaultProps = {
    hours: '0',
    minutes: '10',
    seconds: '0',

}

export default SetTimer;
