import React from 'react';
import ReactDOM, { render } from 'react-dom';

class Step extends React.Component {

    constructor(props) {
        super(props);

        this.state = { 
            step: props.step,
             };

    }

    stepContent(){
        switch(this.state.step){
            case 'main-menu':
                return "Main-menu";
                break;
            case 'instructions':
                return "instructions";
                break;
            case 'set-timer':
                return null;
                break;
            case 'timer':
                    return null;
                    break;
            default:
                return null;            
        }
    }

    render()
    {
        return (
            <div>
                {this.stepContent()}
            </div>
        );
    }
}

export default Step;
