import React from 'react';
import Timer from './Timer';

class SetTimer extends React.Component {

    constructor(props) {
        super(props);       

        this.state = { 
            standardsTime : ['1', '3', '5', '10', '15', '20'],
            customTime: {
                hours: this.props.hours,
                minutes: this.props.minutes,
                seconds: this.props.seconds,
            }
            }; 

    }

    //Format the time
    formatTime(time) {
        return time < 10 ? (`0${time}`) : time;
    }

    standardTimeButton(){
        const values = this.state.standardsTime
        const buttonList = values.map((values) => <li>{values}</li> );
        return <ul>{buttonList}</ul>;

    }

    render()
    {
        return (
            <>
                <div>
                <h2>Standards time</h2> 
                {this.buttonList}




                </div>
                <div id="custom-time">
                <h2>Custom time</h2>
                    <form>
                        <input id="hours" type="number" min="0" max="6" defaultValue={this.state.hours}></input>
                        <input id="minutes" type="number" min="0" max="59" defaultValue={this.state.minutes}></input>
                        <input id= "seconds" type="number" min="0" max="59" defaultValue={this.state.seconds}></input>
                        <button>Validate</button>  
                    </form>   

                </div>
            </>
        );
    }
}


SetTimer.defaultProps = {
    hours: '0',
    minutes: '10',
    seconds: '0',

}

export default SetTimer;
