import React from 'react';

class Step extends React.Component {

    constructor(props) {
        super(props);

        this.state = { 
            step: props.step,
             };   
    }

    setStep(e, step){
        this.setState({step: step});
    }

    mainMenu(){
        return (
            <>
                <button onClick={(e) => this.setStep(e, 'set-timer')}>Set the timer</button>
                <button onClick={(e) => this.setStep(e, 'instructions')}>instructions</button>
            </>
        );
    }

    instructions(){
        return (
            <div>
                Show instructions
            </div>
        );
    }

    setTimer(){
        return (
            <div>
                Timmer settings
            </div>
        );
    }


    stepContent(){
        switch(this.state.step){
            case 'main-menu':
                return this.mainMenu();
                break;
            case 'instructions':
                return this.instructions();
                break;
            case 'set-timer':
                return <div>
                    Set the timer
                </div>;
                break;
            case 'timer':
                    return null;
                    break;
            default:
                return null;            
        }
    }

    render()
    {
        return (
            <div className="Step">
                {this.stepContent()}
            </div>
        );
    }
}

export default Step;
