import React from 'react';

class Step extends React.Component {

    constructor(props) {
        super(props);

        this.state = { 
            step: props.step,
             };   
    }

    setStep(e, step){
        this.setState({step: step});
    }


    stepContent(){
        switch(this.state.step){
            case 'main-menu':
                return (
                    <>
                        <button onClick={(e) => this.setStep(e, 'set-timer')}>Set the timer</button>
                        <button onClick={(e) => this.setStep(e, 'instructions')}>instructions</button>
                    </>
                );
                break;
            case 'instructions':
                return "instructions";
                break;
            case 'set-timer':
                return <div>
                    Set the timer
                </div>;
                break;
            case 'timer':
                    return null;
                    break;
            default:
                return null;            
        }
    }

    render()
    {
        return (
            <div className="Step">
                {this.stepContent()}
            </div>
        );
    }
}

export default Step;
