import React from 'react';

class Timer extends React.Component {

    constructor(props) {
        super(props); 

        //Le timer sera definit par les props qui lui sont passé

        this.state = { 
            whiteTimer: this.props.whiteTimer,
            blackTimer: this.props.blackTimer,
            isClockActive: false,
             };  

    }

    //Format the time
    formatTime(time) {
        return time < 10 ? (`0${time}`) : time;
    }

    render()
    {
        return (
            <>
                <button>{this.state.whiteTimer}</button>
                <button>{this.state.blackTimer}</button>
            </>

        );
    }
}

Timer.defaultProps = {
    whiteTimer: "8",
    blackTimer: "10",
}

export default Timer;
