import React from 'react';

class Timer extends React.Component {

    constructor(props) {
        super(props); 

        //Le timer sera definit par les props qui lui sont passé

        this.state = { 
            whiteTimer: this.props.whiteTimer,
            blackTimer: this.props.blackTimer,
             };  

    }

    render()
    {
        return (
            <>
                <p>{this.state.whiteTimer}</p>
                <p>{this.state.blackTimer}</p>
            </>

        );
    }
}

Timer.defaultProps = {
    whiteTimer: "10",
    blackTimer: "10",
}

export default Timer;
