import React from 'react';
import ReactDOM, { render } from 'react-dom';
import clock from "img/clock.png"
import knight from "img/knight.png"
import click from "img/click.png"


class Instructions extends React.Component {

    constructor(props) {
        super(props);        

    }

    render()
    {
        return (
            <div>
                <h2>Instructions</h2>            
                <ul>                    
                    <li class="instruction">
                        <img src={clock} alt="clock"/>
                        <p>Set the timer</p>
                    </li>
                    <li class="instruction">
                        <img src={knight}alt="chess knight"/>
                        <p>Start the clock, the time start for white player</p>
                    </li>
                    <li class="instruction">
                        <img src={click} alt="click"/>
                        <p>Each player can toggle the clock by clicking on the time or by pressing the spacebar on desktop version</p>
                    </li>
                </ul>      
            </div>
        );
    }
}

export default Instructions;
