import React from 'react';
import ReactDOM, { render } from 'react-dom';
import './index.css';
import logo from "./img/white-logo.png"
import githubLogo from "./img/GitHub-Mark-Light-32px.png"
import Instructions from './Instructions';
import MainMenu from './MainMenu';
import SetupTimer from './MainMenu';
import Timer from './Timer';


class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = { 
            step: 'main-menu',
             };
      }

    //Function that defin the current step displayed
    step() {
        switch (this.state.step) {
            case'main-menu':
                return <MainMenu/>
            case 'instructions':
                return <Instructions/>;
            case 'setup-timer':
                return <SetupTimer/>;
            case 'timer':
                return <Timer/>;
            default:
                return  <MainMenu/>;
        }
    }

    render(){
        return (
            <>
               <header> 
                    <img id="logo" src={logo} alt="chess clock logo"/>
                </header>

                <main>
                    {this.step()}                
                </main>

                <footer>
                    <a href="#"><img src={githubLogo} alt="github logo"/></a>
                    <p>xroll - 2020</p>                
                </footer> 
            </>
        );
    }
}

ReactDOM.render(<App/>,
    document.querySelector('#root')
    );
