import React from 'react';

class SetTimer extends React.Component {

    constructor(props) {
        super(props);       

        this.state = { 
            hours: this.props.hours,
            minutes: this.props.minutes,
            seconds: this.props.seconds,
            }; 

    }

    //Format the time
    formatTime(time) {
        return time < 10 ? (`0${time}`) : time;
    }

    render()
    {
        return (
            <div id="custom-time">
                <form>
                    <input id="hours" type="number" defaultValue={this.state.hours}></input>
                    <input id="minutes" type="number" defaultValue={this.state.minutes}></input>
                    <input id= "seconds" type="number" defaultValue={this.state.seconds}></input>
                </form>              
            </div>
        );
    }
}


SetTimer.defaultProps = {
    hours: '0',
    minutes: '10',
    seconds: '0',

}

export default SetTimer;
